import chai from 'chai';
import { hasMessages } from '../lib/has-messages.js';
import { buildVfile, fileWithNoMessages } from './helpers/vfile.js';
chai.should();
describe('hasMessages', function () {
    it('returns false when passed an empty array', function () {
        hasMessages([]).should.equal(false);
    });
    it('returns false when passed an array of files with no messages', function () {
        hasMessages([
            fileWithNoMessages,
            fileWithNoMessages,
            fileWithNoMessages,
        ]).should.equal(false);
    });
    it('returns true when passed an array containing one file with a message', function () {
        hasMessages([fileWithNoMessages, buildVfile(['a'])]).should.equal(true);
    });
    it('filters messages using the given function', function () {
        hasMessages([buildVfile(['a', 'b', 'c'])], function (message) { return message.source === 'asdf'; }).should.equal(false);
    });
});
