import chai from 'chai';
import { isMarkdownFile } from '../lib/is-markdown-file.js';
chai.should();
describe('isMarkdownFile', function () {
    it('returns true for a file with the extension `.md`', function () {
        isMarkdownFile('test.md').should.equal(true);
    });
    it('returns true for a file with the extension `.markdown`', function () {
        isMarkdownFile('test.markdown').should.equal(true);
    });
    it('returns true for a file with the extension `.mdx`', function () {
        isMarkdownFile('test.mdx').should.equal(true);
    });
    it('returns true for a file with the extension `.MD`', function () {
        isMarkdownFile('test.MD').should.equal(true);
    });
    it('returns false for a file with a non-Markdown extension', function () {
        isMarkdownFile('test.txt').should.equal(false);
    });
});
