import builder from 'junit-report-builder';
export var genJunitReport = function (path, vfiles) {
    var suite = builder.testSuite().name('spellchecker');
    vfiles.forEach(function (file) {
        file.messages.forEach(function (error) {
            var testCase = suite
                .testCase()
                .className(error.ruleId)
                .name(error.name);
            testCase.failure(error.message, error.ruleId);
        });
    });
    builder.writeTo(path);
    console.log("Generated report: ".concat(path));
};
