import flatMap from 'lodash/flatMap.js';
import uniq from 'lodash/uniq.js';
export var toDictionary = function (vfiles) {
    var misspellings = flatMap(vfiles, function (file) {
        var retextSpellMessages = file.messages.filter(function (message) {
            var source = message.source, ruleId = message.ruleId;
            return source === 'retext-spell' && ruleId !== 'overflow';
        });
        return retextSpellMessages.map(function (m) { return m.actual; });
    });
    return uniq(misspellings)
        .sort()
        .map(function (s) { return "".concat(s, "\n"); })
        .join('');
};
